mapper.select:
SELECT contract_id, "type", "year", contract_name, company_name, contract_start, contract_end, invoice_rule, payment_term, total_amount, m01, m02, m03, m04, m05, m06, m07, m08, m09, m10, m11, m12, created_at, updated_at, created_by, updated_by FROM contract_info WHERE 1=1
AND "year" = #{year}
AND "type" = #{type}
ORDER BY "type" , contract_start, contract_end, contract_id
