mapper.select:
SELECT expense_id, expense_name, "year", total_amount, m01, m02, m03, m04, m05, m06, m07, m08, m09, m10, m11, m12, created_at, updated_at, created_by, updated_by FROM expense_info WHERE 1=1
AND "year" = #{year}
ORDER BY expense_id DESC

mapper.delete:
DELETE FROM expense_info
WHERE expense_id IN

    #{expenseId}
