mapper.select:
SELECT user_id, login_id, nickname, role, is_active, password
FROM user_info
WHERE login_id = #{loginId}

SELECT user_id, login_id, nickname, role, is_active
FROM user_info
WHERE 1=1

    AND login_id = #{loginId}


    AND nickname = #{nickname}


    AND role = #{role}


    AND is_active = #{isActive}
